      // timeline.from(loader2, 1.6, {visibility: "visible", x: '-100%', opacity: 0,}).to(loader2, 1, {visibility: "visible", backgroundColor: '#FF0000', color: 'green '});
      gsap.from(loader2, 1.6, {display: "block", y: '100%', opacity: 0,});
