      let loader2 = document.querySelector(numberPN)
      timeline.to(loader1, { y: '-100%', opacity: 0, display: "none"})
      .from(loader2, 1.6, {display: "block", y: '100%', opacity: 0, })
      .to(loader2, 1, { display: "block"});




    },
    odejmij() {
      this.clicker--;
    }
  }
}

